INSERT INTO `recipes` VALUES (12,3,'비빔국수는 가라 비빔면의 시대다','요즘 누가 국수 먹나요 비빔면 사먹으면 끝! 하지만 진짜 맛있는 레시피입니다','https://damulbucket.s3.ap-northeast-2.amazonaws.com/5ebc7028-bb2c-438e-ab64-4e9a04debf66-%EB%B9%84%EB%B9%94%EB%A9%B4%EC%8D%B8%EB%84%A4%EC%9D%BC.webp',47,4,'2025-02-11 14:37:08','2025-02-19 02:02:16',_binary '\0'),(17,19,'홈메이드 삼겹살','삼겹살이 먹고싶지만 외식을 할 상황이 아니라면 집에서 홈메이드 삼겹살 파티를 해보는건 어떨까요?\n애매하게 남은 채소들과 양념들을 곁들인다면 냉장고 정리에도 도움이 될거에요!','https://damulbucket.s3.ap-northeast-2.amazonaws.com/f2a02f5c-181d-41a1-b6ee-cb1531dab70f-test.jpg',49,8,'2025-02-14 13:30:11','2025-02-19 03:11:20',_binary '\0'),(29,3,'닭갈비','제가 만든 닭갈비에요~~~','https://damulbucket.s3.ap-northeast-2.amazonaws.com/8d54a0a4-aa33-4e01-ac67-e6eb52e595c2-KakaoTalk_20250217_220419718.jpg',26,7,'2025-02-19 09:40:44','2025-02-19 03:27:15',_binary '\0'),(30,1,'달걀파스타','달걀이 들어간 파스타입니다! 오리지날 까르보나라 느낌이에요','https://damulbucket.s3.ap-northeast-2.amazonaws.com/8f8b613b-6236-4603-a09f-30f5c68a99c6-thumbnail.jpg',16,1,'2025-02-19 09:58:02','2025-02-19 02:13:53',_binary '\0'),(31,1,'양배추닭가슴살샐러드','양배추는 봄에 진짜 맛있는거 아시죠?!\n요즘 살이 찐거 같아 닭가슴살과 같이 먹을 수 있는 샐러드를 준비해봤습니다!','https://damulbucket.s3.ap-northeast-2.amazonaws.com/133361d5-8e11-407e-a720-bdade41d091e-thumbnail.jpg',15,1,'2025-02-19 10:04:00','2025-02-19 01:04:00',_binary '\0'),(32,1,'오징어채볶음','입맛 없을 땐? 오징어채볶음이면 밥 한공기 뚝-딱 입니다!!!!!','https://damulbucket.s3.ap-northeast-2.amazonaws.com/40eaaf7c-1745-4394-9f56-db51b30a8fc5-thumbnail.jpg',12,0,'2025-02-19 10:08:07','2025-02-19 01:08:07',_binary '\0'),(33,52,'돌아기 소고기무국 아빠표 초간단 저염 무염레시피','무 제철을 맞이해 소고기무국을 끓여봤어요\n제철무를 이용하니 무의 단맛도 좋더라구요! 소고기를 싫어하는 아이들도 맛있게 먹을 수 있는 소고기무국입니다~\n\n','https://damulbucket.s3.ap-northeast-2.amazonaws.com/f785e345-69a4-4eb7-ae3c-45eddedc0c1a-thumbnail.png',14,0,'2025-02-19 10:11:38','2025-02-19 01:11:38',_binary '\0'),(34,3,'달콤쫀득한 맛에 홀릭! 맛있는 바나나 브레드','촉촉함과 쫀쫀함이 나오는 바나나브레드에요 ~~ \n바나나 브레드는 잘못 구우면 비린내가\n살짝 비쳐 끝까지 먹기 어려운 경우가 있죠..ㅠㅠ\n하지만 이 방법은 그러한 걱정 없이\n달콤함과 쫀득함을 끝까지 유지하면서\n맛나게 즐길 수가 있답니다~!','https://damulbucket.s3.ap-northeast-2.amazonaws.com/673aac1a-332e-494b-9ee7-6daf8b6e640b-thumbnail.png',30,2,'2025-02-19 10:29:56','2025-02-19 03:04:35',_binary '\0'),(35,15,'국보급 기밀문서) GS 10,800원 미슐랭 레시피','평생 공유 안할라고 했는데 마음이 바뀌었습니다.','https://damulbucket.s3.ap-northeast-2.amazonaws.com/99441cf6-e594-415f-994f-d60c44373843-MyRecipe.jpg',10,2,'2025-02-21 01:02:17',NULL,_binary '\0');
